mapper.select:
SELECT c.seq ,c.ccg_seq ,(select codeGroup from ccg d where d.seq = c.ccg_seq) as codeGroup ,c.codeName ,c.codeNameEn ,c.useNy ,c.sort ,c.reg_date ,c.mod_date FROM cc c LEFT JOIN ccg d on c.ccg_seq = d.seq where 1=1
and c.reg_date between #{startDate} and #{endDate} and c.mod_date between #{startDate} and #{endDate}
and c.ccg_seq = #{shName} and c.codeName LIKE CONCAT('%', #{shName}, '%')
and c.codeNameEn LIKE CONCAT('%', #{shName}, '%')
and codeGroup LIKE CONCAT('%', #{shName}, '%')
and c.useNy = #{shUseNy} and c.useNy = #{shUseNy}

mapper.insert:

	SELECT last_insert_id()

insert into cc(
ccg_seq
,codeName
,codeNameEn
,useNy
,reg_date
)
values(
#{ccg_seq}
,#{codeName}
,#{codeNameEn}
,#{useNy}
,now()
)
