mapper.select:
	SELECT
	a.seq
	,a.codeGroup
	,a.codeGroupEn
	,a.useNy
	,a.reg_date
	,a.mod_date
	,(select count(b.seq) from cc b where b.ccg_seq = a.seq) as cnt
FROM ccg a
where 1=1

		and a.reg_date between #{shDateSt} and #{shDateFi}
		and a.mod_date between #{shDateSt} and #{shDateFi}


		and a.seq = #{shName}
		and a.codeGroup LIKE CONCAT('%', #{shName}, '%')
		and a.codeGroupEn LIKE CONCAT('%', #{shName}, '%')
		and cnt = #{shName}
		and a.useNy = #{shUseNy}
		and a.useNy = #{shUseNy}
