mapper.select:
SELECT
a.seq
,a.codeGroup
,a.codeGroupEn
,a.useNy
,a.reg_date
,a.mod_date
,(select count(b.seq) from cc b where b.ccg_seq = a.seq) as cnt
FROM ccg a
where 1=1

and a.reg_date between #{startDate} and #{endDate}
and a.mod_date between #{startDate} and #{endDate}



	and a.seq = #{shName}
	and a.codeGroup LIKE CONCAT('%', #{shName}, '%')

	and a.codeGroupEn LIKE CONCAT('%', #{shName}, '%')

	and cnt = #{shName}
	and a.useNy = #{shUseNy}
	and a.useNy = #{shUseNy}

mapper.insert:

	SELECT last_insert_id()

insert into ccg(
	codeGroup
    ,codeGroupEn
    ,useNy
    ,reg_date
)
values(
	#{codeGroup}
	,#{codeGroupEn}
	,#{useNy}
	,now()
)
