	,productName
	,productRating
	,productDiscountPercent
	,productOriginalPrice
	,productDiscountPirce
	,deliveryPrice
	,productCountryOfOrigin
	,productNumber
)
values(
	#{seq}
	,#{productCategory}
	,#{prodcutName}
	,#{productRating}
	,#{productDiscountPercent}
	,#{productOriginalPrice}
	,#{productDiscountPirce}
	,#{deliveryPrice}
	,#{productCountryOfOrigin}
	,#{productNumber}
)

	select last_insert_id()
