mapper.insert:

	SELECT last_insert_id()

insert into member(
	ifmmId
	,ifmmPwd
    ,ifmmName
    ,ifmmDob
    ,ifmmAdminNy
    ,ifmmRating
    ,ifmmGender
    ,ifmmEmail
    ,ifmmTel
    ,ifmmZipcode
    ,ifmmAddress
    ,ifmmDetailedAddress
    ,ifmmRegDate
)
values(
	#{ifmmId}
	,#{ifmmPwd}
	,#{ifmmName}
	,#{ifmmDob}
	,#{ifmmAdminNy}
	,#{ifmmRating}
	,#{ifmmGender}
	,#{ifmmEmail}
	,#{ifmmTel}
	,#{ifmmZipcode}
	,#{ifmmAddress}
	,#{ifmmDetailedAddress}
	,now()
)

mapper.update:
UPDATE member SET  
	ifmmName = #{ifmmName}
	,ifmmId = #{ifmmId}
	,ifmmDob = #{ifmmDob}
	,ifmmAdminNy = #{ifmmAdminNy}
	,ifmmRating = #{ifmmRating}
	,ifmmGender = #{ifmmGender}
	,ifmmEmail = #{ifmmEmail}
	,ifmmTel = #{ifmmTel}
	,ifmmZipcode = #{ifmmZipcode}
	,ifmmAddress = #{ifmmAddress}
	,ifmmDetailedAddress = #{ifmmDetailedAddress}
	,ifmmModDate = now()
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
